// Column.js
import React, { useState } from 'react';

const Column = ({
  title,
  cards,
  onAddCard,
  onRevealCard,
  onDeleteCard,
  onRevealAll,
  onDeleteAll,
  onLike,
  onDislike,
}) => {
  const [cardContent, setCardContent] = useState('');

  const handleAddCard = () => {
    if (cardContent.trim()) {
      onAddCard(cardContent);
      setCardContent(''); // Limpa o campo de texto após adicionar
    } else {
      alert('Por favor, insira algum texto.');
    }
  };

  return (
    <div className="column">
      <h3>{title}</h3>
      <button onClick={onRevealAll}>Revelar Todos</button>
      <button onClick={onDeleteAll}>Deletar Todos</button>

      <div className="cards">
        {cards.map((card, index) => (
          <div key={index} className="card">
            {card.revealed ? (
              <p>{card.content}</p>
            ) : (
              <p>***</p>
            )}
            <button onClick={() => onRevealCard(index)}>Revelar</button>
            <button onClick={() => onDeleteCard(index)}>Deletar</button>
            <button onClick={() => onLike(index)}>Like</button>
            <button onClick={() => onDislike(index)}>Dislike</button>
          </div>
        ))}
      </div>

      {/* Área de texto para inserir novo conteúdo */}
      <textarea
        placeholder="Digite o conteúdo do card..."
        value={cardContent}
        onChange={(e) => setCardContent(e.target.value)}
      />
      <button onClick={handleAddCard}>Adicionar Card</button>
    </div>
  );
};

export default Column;
