import React, { useState } from 'react';

const Column = ({ title, cards, onAddCard, onDeleteCard, onRevealCard, onRevealAll, onDeleteAll, onLike, onDislike }) => {
  const [isAdding, setIsAdding] = useState(false); // Estado para controlar a exibi√ß√£o do campo de texto
  const [newCardContent, setNewCardContent] = useState(''); // Estado para armazenar o conte√∫do do novo card

  const handleAddCardClick = () => {
    setIsAdding(true); // Exibe o campo de texto ao clicar no bot√£o
  };

  const handleAddCardSubmit = () => {
    if (newCardContent.trim()) {
      onAddCard(newCardContent); // Chama a fun√ß√£o de adicionar card
      setNewCardContent('');
      setIsAdding(false);
    }
  };

  return (
    <div className="column">
      <h2>{title}</h2>
      {cards.map((card, index) => (
        <div key={index} className="card">
          {card.revealed ? card.content : 'Texto mascarado, pague-me um caf√© pra liberar ‚òï'}
          <div className="buttons">
            {!card.revealed && (
              <button className="reveal" onClick={() => onRevealCard(index)}>üëÅÔ∏è Revelar</button>
            )}
            <button className="delete" onClick={() => onDeleteCard(index)}>üóëÔ∏è Apagar</button>
          </div>
          {card.userName && <span>por: <b>{card.userName}</b></span>}

          {card.revealed && (
            <div className="like-dislike">
              <button onClick={() => onLike(index)}>üëç Like ({card.likes || 0})</button>
              <button onClick={() => onDislike(index)}>üëé Dislike ({card.dislikes || 0})</button>
            </div>
          )}
        </div>
      ))}
      <div className="controls">
        {isAdding ? (
          <>
            <textarea
              placeholder="Digite o conte√∫do do card..."
              value={newCardContent}
              onChange={(e) => setNewCardContent(e.target.value)}
            />
            <button onClick={handleAddCardSubmit}>‚ûï Adicionar Card</button>
            <button onClick={() => setIsAdding(false)}>‚ùå Cancelar</button>
          </>
        ) : (
          <button className="add" onClick={handleAddCardClick}>‚ûï Adicionar Card</button>
        )}
        <button className="reveal" onClick={onRevealAll}>üëÅÔ∏è Revelar Todos</button>
        <button className="delete" onClick={onDeleteAll}>üóëÔ∏è Apagar Todos</button>
      </div>
    </div>
  );
};

export default Column;
